package ds_general;

import java.util.*;
import java.util.function.Function;
import java.util.stream.Collectors;

public class StringPermutation {

    private static boolean verifyMap (Map<String, Long> countMap , Map<String, Integer> valueMap) {

        for (String co : countMap.keySet()) {
            Long count  = countMap.get(co);
            Integer count2 = valueMap.get(co);

            if (count2 == null) {
                return false;
            }

            if (count2 < count) {
                return false;
            }
        }
        return true;
    }


    public static boolean checkInclusion(String s1, String s2) {

        Map<String, Long> countMap = Arrays.stream(s1.split("")).collect(Collectors.groupingBy(Function.identity(), Collectors.counting()));
        String[] arr = s2.split("");
        Map<String, Integer> valueMap = new HashMap<>();
        int start = -1, end = 0;
        boolean isFound  = false;
        List<Integer> index = new ArrayList<>();
        while (start != end) {

            //start compacting window size.
            if (verifyMap(countMap, valueMap)) {
                start = start +1;

                while (true) {
                    if (countMap.size() == valueMap.size()) {
                        boolean verify = false;
                        for (String c1 : countMap.keySet()) {
                            Integer v1 = valueMap.get(c1);
                            int v2 = Math.toIntExact(countMap.get(c1));

                            if (v1 == null) {
                                verify = false;
                                break;
                            }
                            if (v1 != v2) {
                                verify = false;
                                break;
                            }
                            else {
                                verify = true;
                            }
                        }
                        if (verify) {
                            //index.add(start);
                            return true;
                        }
                    }

                    Integer val = valueMap.get(arr[start]);
                    val = val - 1;
                    if (val == 0) {
                        valueMap.remove(arr[start]);
                    } else {
                        valueMap.put(arr[start], val);
                    }

                    if (!verifyMap(countMap, valueMap)) {
                        break;
                    }

                    start++;
                }
            }

            //add element in window
            if (end >= arr.length) {
                break;
            }
            String val = arr[end];
            Integer c = valueMap.get(val);
            if (valueMap.containsKey(val)) {
                valueMap.put(val, ++c);
            } else {
                valueMap.put(val, 1);
            }
            end++;
        }
        System.out.println(index);
        return false;
    }

    private static boolean checkTwoStringContentIsEqual(String given, String check) {
        int total = Arrays.stream(given.split("")).mapToInt( i -> (int)i.charAt(0)).sum();

        int sum = Arrays.stream(check.split("")).mapToInt( i -> (int)i.charAt(0)).sum();
        return total == sum;
    }

    public static void main(String[] args) {
        String s1 = "ab";
        String s2 = "eidbaooo";

        System.out.println(checkInclusion(s1, s2));

        String s3 = "adc";
        String s4 = "dcda";
        System.out.println(checkInclusion(s3, s4));

        String s5 = "abc";
        String s6 = "dsderefrrgrgbca";
        System.out.println(checkInclusion(s5, s6));

        String s7 = "intention";
        String s8 = "execution";
        System.out.println(checkInclusion(s7, s8));

        String s9 = "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
        String s10 = "";

        System.out.println(checkInclusion(s9, s10));

       String s11 ="hello";
       String s12 ="ooolleoooleh";
        System.out.println(checkInclusion(s11, s12));

       String s13 = "a";
       String s14 = "ab";
        System.out.println(checkInclusion(s13, s14));


        String s15 = "abc";
        String s16 = "cbaebabacd";
        System.out.println(checkInclusion(s15, s16));

    }
}
